import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import AddItems from "../Pages/AddItems";
import Profile from "../Pages/Profile";
import HomePage from "../Pages/HomePage";
import WelcomePage from "../Pages/Welcome";
import SignupPage from "../Pages/SignupPage";
import LoginPage from "../Pages/LoginPage";
import "./App.css"

const App = () => {
  // State to track if user is logged in
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle login (just a simulation)
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div className="app">
        {/* Navigation Bar */}
        <nav>
          <ul>
            {!isLoggedIn && <li><Link to="/">Welcome</Link></li>}
            {!isLoggedIn && <li><Link to="/signup">Signup</Link></li>}
            {!isLoggedIn && <li><Link to="/login">Login</Link></li>}
            {isLoggedIn && <li><Link to="/home-page">Home</Link></li>}
            {isLoggedIn && <li><Link to="/add-items">Add Items</Link></li>}
            {isLoggedIn && <li><Link to="/profile">Profile</Link></li>}
          </ul>
        </nav>

        {/* Main Content */}
        <main className="content-wrapper">
          <Routes>
            <Route path="/" element={<WelcomePage />} />
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/home-page" element={isLoggedIn ? <HomePage /> : <Navigate to="/login" />} />
            <Route path="/add-items" element={isLoggedIn ? <AddItems /> : <Navigate to="/login" />} />
            <Route path="/profile" element={isLoggedIn ? <Profile /> : <Navigate to="/login" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
